function AdoptInfo() {
  const [data, setData] = useState({});

  const fetchData = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8000/adopt/info/item=1`
      );
      console.log(response.data);
      setData(response.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  React.useEffect(() => {
    fetchData();
  }, []);

  return (
    <React.Fragment>
      <a href="" className="backPage">
        <img src="./img/arrow.png" className="mx-5" alt="回上一頁"></img>
      </a>
      <div className="d-flex col-12">
        <div className="petPic col-8 ">
          <img src="" alt="123"></img>
        </div>
        <div>
          <div className="col-4 mt-5">
            <p>{data.animal_variety }</p>
            <p>{data.animal_sex="F"?"女":"男"}</p>
            <p>{data.animal_color }</p>
            <p>已打疫苗</p>
            <p>已除蟲</p>
            <p>目前狀況適合領養</p>
          </div>
          <div className="willingness">
            <button>我有意願領養</button>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
}

export default AdoptInfo;
