import { useState } from "react";

const usePagination = ({ defaultCurrent = 1, pageSize = 20, total }) => {
  const totalPage = Math.ceil(total / pageSize);
  const { current, setCurrent } = useState(defaultCurrent);
  const items = [...Array(totalPage).keys()]
    .map((key) => key + 1)
    .map((page) => ({
      isCurrent: current === page,
      page,
      onClick: () => setCurrent(page),
    }));

  const handleClickNext = () => {
    const nextCurrent = current + 1 > totalPage ? totalPage : current + 1;
    setCurrent(nextCurrent);
  };

  const handleClickPrev = () => {
    const PrevCurrent = current - 1 < 1 ? 1 : current - 1;
    setCurrent(PrevCurrent);
  };

  return (
    <React.Fragment>
      <StyledPagination>
        <PreviousButton onClick={handleClickPrev} />
        {items.map((item) => (
          <StyledItem
            key={item.page}
            isCurrent={item.isCurrent}
            onClick={item.isCurrent}
          >
            <span>{item.page}</span>
          </StyledItem>
        ))}
        <NextButton onClick={handleClickNext} />
      </StyledPagination>
    </React.Fragment>
  );
};

export default usePagination;
