function reducer(state, action) {
  switch (action.type) {
    case "nextPage":
      return { currentPage: state.currentPage + 1 };
    case "prevPage":
      return { currentPage: state.currentPage > 1 ? state.currentPage - 1 : 1 };
    default:
      return state;
  }
}

