import React, { Component } from "react";
import axios from "axios";

class TodoIndex extends Component {
  state = {
    todoList: [
      { todoTableId: 1, title: "Job X", isComplete: 1 },
      { todoTableId: 2, title: "Job Y", isComplete: 0 },
      { todoTableId: 3, title: "Job Z", isComplete: 1 },
      // 先放假資料，在後端還沒寫完或資料庫還沒建置完成的時候，可以先在前端測試
    ],
  };

  // 從後端(http://localhost:8000/todo/list)拿資料
  // 畫面元件加載完成後，會執行 componentDidMount()
  async componentDidMount() {
    let result = await axios.get("http://localhost:8000/todo/list")
    let newState = { ...this.state }
    // console.log(result.data);
    newState.todoList = result.data;
    this.setState(newState)
  }

  render() {
    return (
      <div className="container">
        <h1>
          待辦事項清單
          <a
            href="/Todo/Create"
            className="btn btn-outline-success btn-md float-right"
          >
            新增
          </a>
        </h1>

        <table className="table table-striped table-hover">
          <thead>
            <tr>
              <th>項目名稱</th>
              <th>是否已完工</th>
              <th>&nbsp;</th>
            </tr>
          </thead>
          <tbody>
            {this.state.todoList.map((todoItem,index) => (
              <tr key={index}>
                <td>{todoItem.title}</td>
                <td>
                  <input
                    className="check-box"
                    disabled="disabled"
                    type="checkbox"
                    checked={todoItem.isComplete}
                  />
                </td>
                <td>
                  <span className="float-right">
                    <a
                      href= {`/Todo/Edit/${todoItem.todoTableId}`}
                      className="btn btn-outline-primary btn-sm"
                    >
                      編輯
                    </a>{" "}
                    |
                    <a
                      href={`/Todo/Delete/${todoItem.todoTableId}`}
                      className="btn btn-outline-danger btn-sm"
                    >
                      刪除
                    </a>
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default TodoIndex;
